/** setting footer header */
$(window).resize(function() {
  var footHeight = $('body').outerHeight() - $('header').outerHeight() - $('.content').outerHeight();
  $('footer').css({'height':footHeight});
  $('.fab fa-steam').css({'font-size':70*footHeight/100});
})
$(window).resize();

$(window).resize(function() {
  var colHeight = $('#c1').outerHeight();
  $('#logo1').css({'height':colHeight/3.5});
})
$(window).resize();